import React, { useState } from 'react';

function ClaimRewards() {
  // Example total coins the user has
  const [totalCoins, setTotalCoins] = useState(350);

  // Reward items with their coin costs
  const rewards = [
    { name: 'Next Session Free', cost: 200 },
    { name: 'Movie Ticket', cost: 150 },
    { name: 'Amusement Park Ticket', cost: 300 },
    { name: 'Meal Vouchers', cost: 100 }
  ];

  // Function to claim reward
  const claimReward = (cost) => {
    if (totalCoins >= cost) {
      setTotalCoins(totalCoins - cost);
      alert('Reward claimed!');
    } else {
      alert('Not enough coins to claim this reward.');
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Claim Rewards</h2>
      <p>Total Coins: {totalCoins}</p>

      <div style={{
        display: 'flex',
        justifyContent: 'center',
        flexWrap: 'wrap',
        gap: '1.5rem',
        marginTop: '2rem'
      }}>
        {rewards.map((reward, index) => (
          <div key={index} style={{
            border: '1px solid #ccc',
            padding: '1.5rem',
            borderRadius: '10px',
            width: '250px',
            textAlign: 'center',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
          }}>
            <h3>{reward.name}</h3>
            <p>Cost: {reward.cost} coins</p>
            <button
              onClick={() => claimReward(reward.cost)}
              disabled={totalCoins < reward.cost}
              style={{
                padding: '0.5rem 1rem',
                borderRadius: '5px',
                backgroundColor: totalCoins >= reward.cost ? '#4CAF50' : '#ccc',
                color: 'white',
                border: 'none',
                cursor: totalCoins >= reward.cost ? 'pointer' : 'not-allowed'
              }}
            >
              {totalCoins >= reward.cost ? 'Claim' : 'Not Enough Coins'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ClaimRewards;
