import { useEffect, useState } from "react";

function Plot() {
  const [base, setBase] = useState();
  const [windowSize, setWindowSize] = useState(5); // Default window size
  const [loading, setLoading] = useState(false);

  const fetchImg = async (size) => {
    setLoading(true);
    const url = `http://127.0.0.1:5000/plot?window_size=${size}`;
    try {
      let result = await fetch(url);
      result = await result.json();
      setBase(result.img);
    } catch (error) {
      console.error("Error fetching plot:", error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch the plot when the component mounts with default window size
  useEffect(() => {
    fetchImg(windowSize);
  }, [windowSize]);

  // Handle input change
  const handleInputChange = (event) => {
    setWindowSize(event.target.value);
  };

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    fetchImg(windowSize);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="window_size">Enter Moving Average Window Size:</label>
        <input
          type="number"
          id="window_size"
          name="window_size"
          min="1"
          value={windowSize}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Update Plot</button>
      </form>
      {loading ? (
        <p>Loading...</p>
      ) : (
        base && <img src={`data:image/png;base64,${base}`} alt="plot" />
      )}
    </div>
  );
}

export default Plot;
